mapper.select:

/*	retrieveMaxBoardNo	*/
	SELECT MAX(BOARD.BNO)
	FROM   tb_jw_consulting BOARD

mapper.insert:

INSERT INTO TB_JW_CONSULTING(
	BNO
	, TITLE
	, CONTENT
	, WRITER
	, COUNSELOR
) VALUES (
	SEQ_CONSULTING.NEXTVAL
	, #{title}
	, #{content}
	, #{writer}
	, 'JEWELL MANAGER'
)

mapper.update:

/*	updateBoard	*/
	UPDATE tb_jw_consulting
	SET TITLE = #{title}
	, CONTENT = #{content}
	WHERE BNO = #{bno}


/*	updateBoardHits	*/
	UPDATE tb_jw_consulting
	SET BOARD_HITS          =  BOARD_HITS + 1
	WHERE BNO = #{BNO}

mapper.delete:

/*	deleteBoard	*/
	DELETE
	FROM  tb_jw_consulting
	WHERE 1=1
	AND BNO = #{BNO}

mapper.select:


	SELECT
	    BNO, TITLE, CONTENT, WRITER, COUNSELOR, REGDATE
    from(
	    SELECT /*+INDEX_DESC(tb_jw_consulting pk_consulting) */
	        ROWNUM rn, bno, title, content, writer, counselor, regdate
	    FROM tb_jw_consulting
	    WHERE 






							title like '%'||#{keyword}||'%'


							content like '%'||#{keyword}||'%'


							writer like '%'||#{keyword}||'%'






		ROWNUM <= #{pageNum} * #{amount}
		)
	where RN > (#{pageNum} -1) * #{amount}
